import styled, {createGlobalStyle}from 'styled-components';


export const GlobalStyle = createGlobalStyle`
*{
    padding:0;
    margin:0;
    border:0;
}
`

export const HeaderWrap = styled.div`

 display: flex;
 align-items: center;
 justify-content: space-around;
 background-color: #127E71;
 width:100vmax;
 padding: 30px;
 
 h1{
     color:#fff;
     font-size:20;
     font-family:'Lucida Sans', 'Lucida Sans Regular', 'Lucida Grande', 'Lucida Sans Unicode', Geneva, Verdana, sans-serif;
 }
 img{
     max-width:200px;
     height: 50px;
 }
`

export const FooterWrap = styled.div`
 display:flex;
 justify-content:center;
 align-items:center;
 width:100vmax;
 padding:20px;
 background-color: #212121;
 h1{
     color:#fff;
 }
 img{
     max-width:200px;
     height: 50px;
 }

`

export const ContentWrap = styled.div`
display:flex;
align-items:flex-start;

`

export const ProjectsWarp = styled.div`

display:flex;
padding: 20px;
flex-wrap:wrap;

`

export const CardWarp = styled.div`
background-color: #e5e5e5;
color: #666;
border-radius: 5px;
margin:10px;
width:350px;
overflow:hidden;
font-size:14px;
box-shadow: 0 2px 4px 0 rgba(0,0,0,0.2);

span{
        font-weight:bold;
    }
*{
    margin:0px;
    padding:2px;
}
`
export const CardHeader = styled.div`
padding: 15px;
height: auto;
width:auto;
display:flex;
flex-direction:column;

background-color:#127E71;
color: #fff;
p{
    font-size:12px;
}
`

export const CardDescription = styled.div`

padding:15px;
background-color:#e5e5e5;
`

export const ProjectFilterWrap = styled.div`
    background-color:#005151;
    display:flex;
    flex-direction:column;
    align-items:center;
    padding:50px;
    color: #FFF;
    font-size:10px;
    border-radius:5px;
    margin:30px;

    h2{
        font-size:16;
        font-weight:bold;
    }
    form{
        display:flex;
        flex-direction:column;
        
        label{
            margin-top:10px;
        }
        input{
            height:25px;
            width:auto;
        }
        

        button{
            margin: 10px;
            padding: 10px;
            width:170px;
            color: #FFF;
            background-color:#127E71;
            border:none;
            

        }



    }

`
