import {Component} from 'react';
import styed from 'styled-components'

const CardWarp = styled.div`
    background-color: #e5e5e5;
    color: #666;
    border-radius: 5px;
    margin:10px;
    width:350px;
    overflow:hidden;
    font-size:14px;
    box-shadow: 0 2px 4px 0 rgba(0,0,0,0.2);

    span{
            font-weight:bold;
        }
    *{
        margin:0px;
        padding:2px;
}
`
const CardHeader = styled.div`
    padding: 15px;
    height: auto;
    width:auto;
    display:flex;
    flex-direction:column;

    background-color:#127E71;
    color: #fff;
    p{
        font-size:12px;
    }
`

const CardDescription = styled.div`
    padding:15px;
    background-color:#e5e5e5;
`

class ProjectCard extends Component{
    render() {
        return (
            <CardWarp>
            <CardHeader>
                <p>ID #000000</p>
                <h3>Nome do Projeto</h3>
            </CardHeader>
            <CardDescription>
                <p><span>Autor:</span> Thales Azevedo</p>
                <p><span>Descrição:</span> O projeto com tal nome tem a roposta de pesquisar tal assunto e obter resutados que podem causar tais impactos.</p>
                <p><span>N de Arquivos:</span> 10</p>
                <p><span>Ultima Atualização:</span> 00/00/0000</p>
            </CardDescription>
            </CardWarp>
        )
    }
}

export default ProjectCard
