 display: flex;
 align-items: center;
 justify-content: space-around;
 background-color: #127E71;
 
 h1{
     color:#fff;
     font-size:20;
     font-family:'Lucida Sans', 'Lucida Sans Regular', 'Lucida Grande', 'Lucida Sans Unicode', Geneva, Verdana, sans-serif;
 }
 img{
     max-width:200px;
     height: 50px;
 }
`

export const FooterWrap = styled.div`
 display:flex;
 justify-content:center;
 align-items:center;
 height:100px;
 padding:0px;
 margin:0px;
 background-color: #212121;
 h1{
     color:#fff;
 }
 img{
     max-width:200px;
     height: 50px;
 }

`

export const ContentWrap = styled.div`
display:flex;
align-items:center;
`

export const ProjectsWarp = styled.div`

display:flex;
padding: 30px;
flex-wrap:wrap;

`

export const CardWarp = styled.div`
background-color: #e5e5e5;
color: #666;
border-radius: 5px;
margin:15px;
width:350px;
overflow:hidden;
font-size:14px;
box-shadow: 0 2px 4px 0 rgba(0,0,0,0.2);

span{
        font-weight:bold;
    }
*{
    margin:0px;
    padding:2px;
}
`
export const CardHeader = styled.div`
padding: 15px;
height: auto;
width:auto;
display:flex;
flex-direction:column;

background-color:#127E71;
color: #fff;
p{
    font-size:12px;
}
`

export const CardDescription = styled.div`

padding:15px;
background-color:#e5e5e5;
`

export const ProjectFilterWrap = styled.div`
    background-color:#005151;
    display:flex;
    flex-direction:column;
    align-items:center;
    padding:50px;
    color: #FFF;
    font-size:12px;
    border-radius:5px;

    h2{
        font-size:16;
        font-weight:bold;
    }
    form{
        display:flex;
        flex-direction:column;
        input{
            height:25px;

        }
    }

`
