box-shadow: 0 2px 4px 0 rgba(0,0,0,0.2);

span{
        font-weight:bold;
    }
div{

    
    padding: 15px;
    height: auto;
    width:auto;
    display:flex;
    flex-direction:column;

    background-color:#127E71;
    color: #fff;

    *{
        margin:0px;
        padding:0px;
    }

    p{
        font-size:12px;
    }
}
`
