import styled, {createGlobalStyle} from 'styled-components'

const GlobalStyle = createGlobalStyle`
    *{
        padding:0;
        margin:0;
        border:0;
    }
` 

const Warpper = styled.div`
    font-family: 'Roboto', sans-serif;
    display:flex;
    height:100vh;
    flex-direction: column;
    justify-content: space-between;
    h1{
        padding-left:30px;
        padding-top:30px;
        color:#005151;
    }

`

export const HeaderWrap = styled.div`

 display: flex;
 align-items: center;
 justify-content: space-around;
 background-color: #127E71;
 width:100vmax;
 padding: 30px; 
 
 h1{
     color:#fff;
     font-size:20;
     font-weight:bold;
     padding:0; 
 }
 img{
     max-width:220px;
 }
`

export const FooterWrap = styled.div`
 display:flex;
 justify-content:center;
 align-items:center;
 width:100vmax;
 padding:20px;
 background-color: #212121;
 h1{
     color:#fff;
 }
 img{
     max-width:200px;
     height: 50px;
 }

`

export const LoginWrap = styled.div`

    display:flex;
    flex-direction:column;
    align-items:center;
    color: #FFF;
    font-size:12px;
    border-radius:5px;

    h2{
        font-size:18px;
        font-weight:300;
    }
    form{
        display:flex;
        flex-direction:column;
        align-items:flex-start;
        
        label{
            margin-top:10px;
        }
        input{
            height:25px;
            width:auto;
        }

        button{
            margin: 10px;
            padding: 10px;
            width:150px;
            color: #FFF;
            background-color:#005151; 
            border:none;
        }
    }
`
