import styled from 'styled-components';


export const HeaderWrap = styled.div`

 display: flex;
 align-items: center;
 justify-content: space-around;
 background-color: #127E71;
 width:100vmax;
 padding: 30px;
 
 h1{
     color:#fff;
     font-size:20;
     font-weight:bold;
     padding:0;
 }
 img{
     max-width:220px;
 }
`

export const FooterWrap = styled.div`
 display:flex;
 justify-content:center;
 align-items:center;
 width:100vmax;
 padding:20px;
 background-color: #212121;
 h1{
     color:#fff;
 }
 img{
     max-width:200px;
     height: 50px;
 }

`

export const LoginWrap = styled.div`

    display:flex;
    flex-direction:column;
    align-items:center;
    color: #FFF;
    font-size:12px;
    border-radius:5px;

    h2{
        font-size:18px;
        font-weight:300;
    }
    form{
        display:flex;
        flex-direction:column;
        align-items:flex-start;
        
        label{
            margin-top:10px;
        }
        input{
            height:25px;
            width:auto;
        }

        button{
            margin: 10px;
            padding: 10px;
            width:150px;
            color: #FFF;
            background-color:#005151; 
            border:none;
        }
    }
`
